package Basic;

public class SwitchCase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i=5;
		switch (i) {
		case 2: {
			
			System.out.println("Value is 2");
			break;
		}
		case 5:{
			System.out.println("Value is 5");
			break;
		}
		default:
			System.out.println("Not from ");
		}
	}

}
